async () => {
  debugger;
  let userdata = await Fetch_otp_details.run({
    email: email.text
  });
  storeValue("userData", userdata[0]);
  if (userdata.length > 0) {
    let otp = Math.floor(1000 + Math.random() * 9000);
    generate_otp.run({
      'emp_id': userdata[0].emp_id,
      'otp': otp
    });
    send_otp_email.run({
      'email': email.text,
      'otp': otp
    });
    storeValue("otp", otp);
  }
}
